    echo "</html>";
} >/etc/advandz/www/index.php

# Configure Firewall
firewall-cmd --permanent --zone=public --add-service=http >> /dev/null 2>&1;
firewall-cmd --permanent --zone=public --add-service=https >> /dev/null 2>&1;
firewall-cmd --permanent --add-port=2086/tcp >> /dev/null 2>&1;
firewall-cmd --permanent --add-port=2087/tcp >> /dev/null 2>&1;
firewall-cmd --permanent --add-port=80/tcp >> /dev/null 2>&1;
firewall-cmd --permanent --add-port=443/tcp >> /dev/null 2>&1;
firewall-cmd --permanent --add-port=8080/tcp >> /dev/null 2>&1;
firewall-cmd --permanent --add-port=4343/tcp >> /dev/null 2>&1;
firewall-cmd --reload >> /dev/null 2>&1;

# Configure Apache
cp /etc/httpd/conf/httpd.conf /etc/httpd/conf/httpd.conf.bak >> /dev/null 2>&1;
rm -rf /etc/httpd/conf/httpd.conf >> /dev/null 2>&1;
rm -rf /etc/httpd/conf.modules.d/10-fcgid.conf >> /dev/null 2>&1;
cp installers/centos/config/httpd.conf /etc/httpd/conf/httpd.conf >> /dev/null 2>&1;
cp installers/centos/config/10-fcgid.conf /etc/httpd/conf.modules.d/10-fcgid.conf >> /dev/null 2>&1;
yum -y install mod_fcgid >> /dev/null 2>&1;
yum -y install mod_proxy_fcgi >> /dev/null 2>&1;
mkdir /etc/httpd/sites-available >> /dev/null 2>&1;
mkdir /etc/httpd/sites-enabled >> /dev/null 2>&1;

# Create Hostname Vhost
{
    echo "<VirtualHost *:8080>";
    echo "  ServerName $HOSTNAME";
    echo "  DirectoryIndex index.html index.php";
    echo "  DocumentRoot /etc/advandz/www";
    echo "  ErrorLog /etc/advandz/logs/error.log";
    echo "  CustomLog /etc/advandz/logs/access.log combined";
    echo "  ScriptAlias /cgi-bin/ \"/etc/advandz/www/cgi-bin/\"";
    echo "  ProxyPassMatch ^/(.+\.(hh|php)(/.*)?)$ fcgi://127.0.0.1:9001/etc/advandz/www/\$1";
    echo "</VirtualHost>";
} >/etc/httpd/sites-enabled/00-$HOSTNAME.conf

# Generate Control Panel SSL
sudo openssl req -subj "/CN=$HOSTNAME/O=Advandz/C=US" -x509 -nodes -days 365 -newkey rsa:2048 -keyout /etc/advandz/ssl/server.key -out /etc/advandz/ssl/server.crt >> /dev/null 2>&1;

# Configure Nginx
cp /etc/nginx/nginx.conf /etc/nginx/nginx.conf.bak
rm -rf /etc/nginx/nginx.conf
rm -rf /etc/nginx/fastcgi.conf
cp installers/centos/config/nginx.conf /etc/nginx/nginx.conf
cp installers/centos/config/fastcgi.conf /etc/nginx/fastcgi.conf
setvalue "worker_connections" "$WORKER_CONNECTIONS;" " " /etc/nginx/nginx.conf

# Configure Varnish
cp /etc/varnish/varnish.params /etc/varnish/varnish.params.bak
rm -rf /etc/varnish/varnish.params
cp installers/centos/config/varnish.params /etc/varnish/varnish.params

# Restart services
service httpd restart >> /dev/null 2>&1;
service nginx restart >> /dev/null 2>&1;
service varnish restart >> /dev/null 2>&1;